contracts/abstracts/PALMManagerStorage.s.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import {IPALMManager} from "../interfaces/IPALMManager.sol";
import {
    IArrakisV2
} from "@arrakisfi/v2-core/contracts/interfaces/IArrakisV2.sol";
import {
    IERC20,
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {
    OwnableUpgradeable
} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {
    PausableUpgradeable
} from "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import {
    EnumerableSet
} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import {VaultInfo} from "../structs/SPALMManager.sol";

// solhint-disable-next-line max-states-count
abstract contract PALMManagerStorage is
    IPALMManager,
    OwnableUpgradeable,
    PausableUpgradeable
{
    using SafeERC20 for IERC20;
    using Address for address payable;
    using EnumerableSet for EnumerableSet.Bytes32Set;
    using EnumerableSet for EnumerableSet.AddressSet;

    // #region PALMTerms.

    address public immutable terms;

    // #endregion PALMTerms.

    // #region PALMTerms Market Making Duration.

    uint256 public immutable termDuration;

    // #endregion PALMTerms Market Making Duration.

    // #region manager Fee BPS.

    uint16 public immutable managerFeeBPS;

    // #endregion manager Fee BPS.

    // #region whitelisted strategies.

    EnumerableSet.Bytes32Set internal _whitelistedStrat;

    // #endregion whitelisted strategies.

    // #region operators.

    EnumerableSet.AddressSet internal _operators;

    // #endregion operators.

    // #region vaults related data.

    mapping(address => VaultInfo) public vaults;

    // #endregion vaults related data.

    // #region gelato bots.

    address payable public gelatoFeeCollector;

    // #endregion gelato bots.

    // #region modifiers.

    modifier onlyPALMTerms() {
        require(msg.sender == terms, "PALMManager: only PALMTerms");
        _;
    }

    modifier onlyVaultOwner(address vault) {
        require(
            IArrakisV2(vault).owner() == msg.sender,
            "PALMManager: only vault owner"
        );
        _;
    }

    modifier onlyManagedVaults(address vault) {
        require(vaults[vault].termEnd != 0, "PALMManager: Vault not managed");
        _;
    }

    modifier onlyOperators() {
        require(_isOperator(msg.sender), "PALMManager: no operator");
        _;
    }

    modifier requireAddressNotZero(address addr) {
        require(addr != address(0), "PALMManager: address Zero");
        _;
    }

    // #endregion modifiers.

    // #region constructor.

    constructor(
        address terms_,
        uint256 termDuration_,
        uint16 _managerFeeBPS_
    ) {
        terms = terms_;
        termDuration = termDuration_;
        managerFeeBPS = _managerFeeBPS_;
    }

    // #endregion constructor.

    // #region initialize function.

    function initialize(address owner_, address gelatoFeeCollector_)
        external
        initializer
    {
        require(owner_ != address(0), "PALMManager: owner is address zero");
        require(
            gelatoFeeCollector_ != address(0),
            "PALMManager: gelatoFeeCollector is address zero"
        );
        _transferOwnership(owner_);
        __Pausable_init();
        gelatoFeeCollector = payable(gelatoFeeCollector_);
        emit SetGelatoFeeCollector(gelatoFeeCollector_);
    }

    // #endregion initialize function.

    // #region permissioned owner functions.

    function pause() external override onlyOwner {
        _pause();
    }

    function unpause() external override onlyOwner {
        _unpause();
    }

    // #endregion permissioned owner functions.

    /// @notice add vault to manage
    /// @param vault_ Arrakis V2 vault address
    /// @param datas_ metadata that be used by PALM to manage vault
    /// @param strat_ strategy type chosen by client
    /// @dev only callable by Terms and only for Terms owned vault.
    function addVault(
        address vault_,
        bytes calldata datas_,
        string calldata strat_
    )
        external
        payable
        override
        whenNotPaused
        requireAddressNotZero(vault_)
        onlyPALMTerms
        onlyVaultOwner(vault_)
    {
        _addVault(vault_, datas_, strat_);
        if (msg.value > 0) _fundVaultBalance(vault_);
    }

    /// @notice remove vault from management
    /// @param vault_ Arrakis V2 vault address
    /// @param to_ address that will left over balance.
    /// @dev only callable by Terms and only for Terms owned vault.
    function removeVault(address vault_, address payable to_)
        external
        override
        whenNotPaused
        requireAddressNotZero(vault_)
        onlyPALMTerms
        onlyManagedVaults(vault_)
    {
        _removeVault(vault_, to_);
    }

    /// @notice for setting managed vault meta data
    /// @param vault_ Arrakis V2 vault address
    /// @param data_ metadata used by PALM to manage the vault
    /// @dev only callable by Terms and only for Terms owned vault.
    function setVaultData(address vault_, bytes calldata data_)
        external
        override
        whenNotPaused
        requireAddressNotZero(vault_)
        onlyPALMTerms
        onlyManagedVaults(vault_)
    {
        _setVaultData(vault_, data_);
    }

    /// @notice for changing strategy type
    /// @param vault_ Arrakis V2 vault address
    /// @param strat_ strategy type chosen by client
    /// @dev only callable by Terms and only for Terms owned vault.
    function setVaultStratByName(address vault_, string calldata strat_)
        external
        override
        whenNotPaused
        requireAddressNotZero(vault_)
        onlyPALMTerms
        onlyManagedVaults(vault_)
    {
        _setVaultStrat(vault_, keccak256(abi.encodePacked(strat_)));
    }

    /// @notice for setting gelato fee collector
    /// @param gelatoFeeCollector_ new gelato fee collector address
    /// @dev only callable by owner
    function setGelatoFeeCollector(address payable gelatoFeeCollector_)
        external
        override
        whenNotPaused
        onlyOwner
        requireAddressNotZero(gelatoFeeCollector_)
    {
        require(
            gelatoFeeCollector != gelatoFeeCollector_,
            "PALMManager: gelatoFeeCollector"
        );
        gelatoFeeCollector = gelatoFeeCollector_;
        emit SetGelatoFeeCollector(gelatoFeeCollector_);
    }

    /// @notice for setting manager fee
    /// @param vault_ Arrakis V2 vault address
    /// @dev only callable by owner
    function setManagerFeeBPS(address vault_)
        external
        override
        whenNotPaused
        onlyPALMTerms
    {
        IArrakisV2(vault_).setManagerFeeBPS(managerFeeBPS);
        emit SetManagerFeeBPS(vault_, managerFeeBPS);
    }

    /// @notice for adding operators
    /// @param operators_ list of operators to add
    /// @dev only callable by owner
    function addOperators(address[] calldata operators_)
        external
        override
        whenNotPaused
        onlyOwner
    {
        for (uint256 i; i < operators_.length; ++i) {
            require(
                operators_[i] != address(0) && _operators.add(operators_[i]),
                "PALMManager: operator"
            );
        }

        emit AddOperators(operators_);
    }

    /// @notice for removing operators
    /// @param operators_ list of operators to remove
    /// @dev only callable by owner
    function removeOperators(address[] calldata operators_)
        external
        override
        whenNotPaused
        onlyOwner
    {
        _removeOperators(operators_);
    }

    /// @notice for withdrawing fee earn as manager
    /// @param tokens_ list of tokens where withdrawing fees
    /// @param to_ receiver of fees
    /// @dev only callable by owner
    function withdrawFeesEarned(address[] calldata tokens_, address to_)
        external
        override
        whenNotPaused
        onlyOwner
    {
        for (uint256 i; i < tokens_.length; i++) {
            uint256 balance = IERC20(tokens_[i]).balanceOf(address(this));
            if (balance > 0) {
                IERC20(tokens_[i]).safeTransfer(to_, balance);
            }
        }
    }

    /// @notice for withdrawing vault fund balance
    /// @param vault_ Arrakis V2 vault address
    /// @param amount_ amount of balance to retrieve
    /// @param to_ receiver of balance
    /// @dev only callable by palm term and managed vault
    function withdrawVaultBalance(
        address vault_,
        uint256 amount_,
        address payable to_
    )
        external
        override
        whenNotPaused
        requireAddressNotZero(vault_)
        onlyPALMTerms
        onlyManagedVaults(vault_)
        requireAddressNotZero(address(to_))
    {
        _withdrawVaultBalance(vault_, amount_, to_);
    }

    /// @notice fund vault balance
    /// @param vault_ Arrakis V2 vault address
    /// @dev only for managed vault
    function fundVaultBalance(address vault_)
        external
        payable
        override
        whenNotPaused
        onlyManagedVaults(vault_)
    {
        _fundVaultBalance(vault_);
    }

    /// @notice fund vault balance
    /// @param vault_ Arrakis V2 vault address
    /// @dev only for managed vault
    function renewTerm(address vault_)
        external
        override
        whenNotPaused
        onlyPALMTerms
        requireAddressNotZero(vault_)
        onlyManagedVaults(vault_)
    {
        emit SetTermEnd(
            vault_,
            vaults[vault_].termEnd,
            // solhint-disable-next-line not-rely-on-time
            vaults[vault_].termEnd = block.timestamp + termDuration
        );
    }

    /// @notice whitelist strategy as owner
    /// @param strat_ strategy type to whitelist
    /// @dev only for managed vault
    function whitelistStrat(string calldata strat_)
        external
        whenNotPaused
        onlyOwner
    {
        bytes32 stratB32 = keccak256(abi.encodePacked(strat_));
        require(
            stratB32 != keccak256(abi.encodePacked("")),
            "PALMManager: empty string"
        );
        require(
            !_whitelistedStrat.contains(stratB32),
            "PALMManager: strat whitelisted."
        );
        _whitelistedStrat.add(stratB32);

        emit WhitelistStrat(strat_);
    }

    /// @notice get whitelisted list of strategies
    function getWhitelistedStrat()
        external
        view
        override
        returns (bytes32[] memory)
    {
        return _whitelistedStrat.values();
    }

    /// @notice get vault info
    /// @param vault_ Arrakis V2 vault address
    /// @return vaultInfo data related to Arrakis V2 vault managed
    function getVaultInfo(address vault_)
        external
        view
        override
        requireAddressNotZero(vault_)
        returns (VaultInfo memory)
    {
        return vaults[vault_];
    }

    /// @notice get list of operators
    /// @return operators array of address representing operators
    function getOperators() external view override returns (address[] memory) {
        return _operators.values();
    }

    // #region internal functions.

    function _addVault(
        address vault_,
        bytes calldata datas_,
        string calldata strat_
    ) internal {
        bytes32 stratEncoded = keccak256(abi.encodePacked(strat_));
        require(
            _whitelistedStrat.contains(stratEncoded),
            "PALMManager: Not whitelisted"
        );
        require(
            vaults[vault_].termEnd == 0,
            "PALMManager: Vault already added"
        );
        vaults[vault_].datas = datas_;
        vaults[vault_].strat = stratEncoded;

        // solhint-disable-next-line not-rely-on-time
        vaults[vault_].termEnd = block.timestamp + termDuration;

        emit AddVault(vault_, datas_, strat_);
    }

    function _fundVaultBalance(address vault_) internal {
        require(msg.value > 0, "PALMManager: cannot fund with 0");
        vaults[vault_].balance += msg.value;
        emit UpdateVaultBalance(vault_, vaults[vault_].balance);
    }

    function _removeVault(address vault_, address payable to_) internal {
        uint256 balance = vaults[vault_].balance;

        IArrakisV2(vault_).withdrawManagerBalance();

        delete vaults[vault_];

        if (balance > 0) to_.sendValue(balance);

        emit RemoveVault(vault_, balance);
    }

    function _setVaultData(address vault_, bytes memory data_) internal {
        require(
            keccak256(vaults[vault_].datas) != keccak256(data_),
            "PALMManager: data"
        );

        vaults[vault_].datas = data_;

        emit SetVaultData(vault_, data_);
    }

    function _setVaultStrat(address vault_, bytes32 strat_) internal {
        require(vaults[vault_].strat != strat_, "PALMManager: strat");

        require(
            _whitelistedStrat.contains(strat_),
            "PALMManager: strat not whitelisted."
        );

        vaults[vault_].strat = strat_;

        emit SetVaultStrat(vault_, strat_);
    }

    function _removeOperators(address[] memory operators_) internal {
        for (uint256 i; i < operators_.length; ++i) {
            require(
                _operators.remove(operators_[i]),
                "PALMManager: no operator"
            );
        }

        emit RemoveOperators(operators_);
    }

    function _withdrawVaultBalance(
        address vault_,
        uint256 amount_,
        address payable to_
    ) internal {
        uint256 oldBalance = vaults[vault_].balance;
        require(
            oldBalance >= amount_,
            "PALMManager: amount exceeds available balance"
        );
        uint256 newBalance = oldBalance - amount_;
        vaults[vault_].balance = newBalance;
        to_.sendValue(amount_);

        emit WithdrawVaultBalance(vault_, amount_, to_, newBalance);
    }

    function _isOperator(address operator_)
        internal
        view
        requireAddressNotZero(operator_)
        returns (bool)
    {
        return _operators.contains(operator_);
    }

    // #endregion internal functions.
}

contracts/abstracts/PALMTermsStorage.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import {IPALMTerms} from "../interfaces/IPALMTerms.sol";
import {
    IArrakisV2Factory
} from "@arrakisfi/v2-core/contracts/interfaces/IArrakisV2Factory.sol";
import {
    IArrakisV2Resolver
} from "@arrakisfi/v2-core/contracts/interfaces/IArrakisV2Resolver.sol";
import {
    IArrakisV2
} from "@arrakisfi/v2-core/contracts/interfaces/IArrakisV2.sol";
import {IPALMManager} from "../interfaces/IPALMManager.sol";
import {
    IERC20,
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {
    OwnableUpgradeable
} from "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import {
    ReentrancyGuardUpgradeable
} from "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import {
    EnumerableSet
} from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

// solhint-disable-next-line max-states-count
abstract contract PALMTermsStorage is
    IPALMTerms,
    OwnableUpgradeable,
    ReentrancyGuardUpgradeable
{
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.AddressSet;

    mapping(address => EnumerableSet.AddressSet) internal _vaults;

    IArrakisV2Factory public immutable v2factory;
    address public termTreasury;
    address public manager;
    uint16 public emolument;
    /// @dev can be used in the future
    IArrakisV2Resolver public resolver;
    mapping(address => address) public delegateByVaults;

    // #region no left over.

    modifier collectLeftOver(IERC20 token0_, IERC20 token1_) {
        uint256 token0Balance = token0_.balanceOf(address(this));
        uint256 token1Balance = token1_.balanceOf(address(this));
        _;
        uint256 leftOver0 = token0_.balanceOf(address(this)) - token0Balance;
        uint256 leftOver1 = token1_.balanceOf(address(this)) - token1Balance;
        if (leftOver0 > 0) token0_.safeTransfer(msg.sender, leftOver0);
        if (leftOver1 > 0) token1_.safeTransfer(msg.sender, leftOver1);
    }

    modifier requireAddressNotZero(address addr) {
        require(addr != address(0), "PALMTerms: address Zero");
        _;
    }

    modifier requireIsOwner(address vault_) {
        require(_vaults[msg.sender].contains(vault_), "PALMTerms: not owner");
        _;
    }

    modifier requireDelegateWhenDelegateExistsOtherwiseRequireIsOwner(
        address vault_
    ) {
        address delegate = delegateByVaults[vault_];
        if (delegate != address(0))
            require(msg.sender == delegate, "PALMTerms: no delegate");
        else
            require(
                _vaults[msg.sender].contains(vault_),
                "PALMTerms: not owner"
            );
        _;
    }

    // #endregion no left over.

    constructor(IArrakisV2Factory v2factory_) {
        v2factory = v2factory_;
    }

    function initialize(
        address owner_,
        address termTreasury_,
        uint16 emolument_,
        IArrakisV2Resolver resolver_
    ) external initializer {
        require(emolument < 10000, "PALMTerms: emolument >= 100%.");
        require(owner_ != address(0), "PALMTerms: owner is address zero.");
        require(
            termTreasury_ != address(0),
            "PALMTerms: term treasury is address zero."
        );
        _transferOwnership(owner_);
        termTreasury = termTreasury_;
        emolument = emolument_;
        resolver = resolver_;
        emit SetTermTreasury(address(0), termTreasury_);
        emit SetEmolument(0, emolument_);
        emit SetResolver(IArrakisV2Resolver(address(0)), resolver_);
    }

    // #region setter.

    /// @notice sets emolument percentage
    /// @param emolument_ new emolument
    /// @dev only callable by owner
    function setEmolument(uint16 emolument_) external onlyOwner {
        require(
            emolument_ < emolument,
            "PALMTerms: new emolument >= old emolument"
        );
        emit SetEmolument(emolument, emolument = emolument_);
    }

    /// @notice sets term treasury
    /// @param termTreasury_ address of fess receiver
    /// @dev only callable by owner
    function setTermTreasury(address termTreasury_)
        external
        onlyOwner
        requireAddressNotZero(termTreasury_)
    {
        require(
            termTreasury != termTreasury_,
            "PALMTerms: already term treasury"
        );
        emit SetTermTreasury(termTreasury, termTreasury = termTreasury_);
    }

    /// @notice sets resolver address
    /// @param resolver_ new resolver
    /// @dev only callable by owner
    function setResolver(IArrakisV2Resolver resolver_)
        external
        onlyOwner
        requireAddressNotZero(address(resolver_))
    {
        require(
            address(resolver) != address(resolver_),
            "PALMTerms: already resolver"
        );
        emit SetResolver(resolver, resolver = resolver_);
    }

    /// @notice sets manager_ address
    /// @param manager_ new manager
    /// @dev only callable by owner
    function setManager(address manager_)
        external
        override
        onlyOwner
        requireAddressNotZero(manager_)
    {
        require(manager_ != manager, "PALMTerms: already manager");
        emit SetManager(manager, manager = manager_);
    }

    // #endregion setter.

    // #region vault config as admin.

    /// @notice for adding new Pools on Arrakis V2 vault
    /// @param vault_ Arrakis V2 vault
    /// @param feeTiers_ feeTiers to add
    /// @dev only callable by owner of the vault
    function addPools(IArrakisV2 vault_, uint24[] calldata feeTiers_)
        external
        override
        requireAddressNotZero(address(vault_))
        requireIsOwner(address(vault_))
    {
        vault_.addPools(feeTiers_);

        emit LogAddPools(msg.sender, address(vault_), feeTiers_);
    }

    /// @notice for removing Pools on Arrakis V2 vault
    /// @param vault_ Arrakis V2 vault
    /// @param pools_ pools to remove
    /// @dev only callable by owner of the vault
    function removePools(IArrakisV2 vault_, address[] calldata pools_)
        external
        override
        requireAddressNotZero(address(vault_))
        requireIsOwner(address(vault_))
    {
        vault_.removePools(pools_);

        emit LogRemovePools(msg.sender, address(vault_), pools_);
    }

    /// @notice for whitelisting routers on Arrakis V2 vault
    /// @param vault_ Arrakis V2 vault
    /// @param routers_ routers to whitelist
    /// @dev only callable by owner of the vault
    function whitelistRouters(IArrakisV2 vault_, address[] calldata routers_)
        external
        override
        requireAddressNotZero(address(vault_))
        requireIsOwner(address(vault_))
    {
        vault_.whitelistRouters(routers_);

        emit LogWhitelistRouters(msg.sender, address(vault_), routers_);
    }

    /// @notice for blacklist routers on Arrakis V2 vault
    /// @param vault_ Arrakis V2 vault
    /// @param routers_ routers to blacklist
    /// @dev only callable by owner of the vault
    function blacklistRouters(IArrakisV2 vault_, address[] calldata routers_)
        external
        override
        requireAddressNotZero(address(vault_))
        requireIsOwner(address(vault_))
    {
        vault_.blacklistRouters(routers_);

        emit LogBlacklistRouters(msg.sender, address(vault_), routers_);
    }

    // #endregion vault config as admin.

    // #region manager config as vault owner.
    /// @notice set Arrakis V2 vault meta data
    /// @param vault_ Arrakis V2 vault
    /// @param data_ metadata to use during market making
    /// @dev only callable by delegate of the vault by default or otherwise owner
    function setVaultData(address vault_, bytes calldata data_)
        external
        override
        requireAddressNotZero(vault_)
        requireDelegateWhenDelegateExistsOtherwiseRequireIsOwner(vault_)
    {
        IPALMManager(manager).setVaultData(vault_, data_);

        address delegate = delegateByVaults[vault_];

        emit LogSetVaultData(
            delegate != address(0) ? delegate : msg.sender,
            vault_,
            data_
        );
    }

    /// @notice set Arrakis V2 vault strategy
    /// @param vault_ Arrakis V2 vault
    /// @param strat_ strategy to apply during market making
    /// @dev only callable by delegate of the vault by default or otherwise owner
    function setVaultStratByName(address vault_, string calldata strat_)
        external
        override
        requireAddressNotZero(vault_)
        requireDelegateWhenDelegateExistsOtherwiseRequireIsOwner(vault_)
    {
        IPALMManager(manager).setVaultStratByName(vault_, strat_);

        address delegate = delegateByVaults[vault_];

        emit LogSetVaultStratByName(
            delegate != address(0) ? delegate : msg.sender,
            vault_,
            strat_
        );
    }

    /// @notice set delegate for managing strategy of Arrakis V2 vault
    /// @param vault_ Arrakis V2 vault
    /// @param delegate_ delegated strategy setter of Arrakis V2 vault
    /// @dev only callable by owner of vault
    function setDelegate(address vault_, address delegate_)
        external
        override
        requireIsOwner(vault_)
    {
        _setDelegate(vault_, delegate_);

        emit LogSetDelegate(msg.sender, vault_, delegate_);
    }

    /// @notice for withdrawing vault balance
    /// @param vault_ Arrakis V2 vault
    /// @param amount_ amount of balance that receiver will get back
    /// @param to_ address of the receiver of balance
    /// @dev only callable by owner of vault
    function withdrawVaultBalance(
        address vault_,
        uint256 amount_,
        address payable to_
    ) external override requireAddressNotZero(vault_) requireIsOwner(vault_) {
        IPALMManager manager_ = IPALMManager(manager);
        manager_.withdrawVaultBalance(vault_, amount_, to_);

        emit LogWithdrawVaultBalance(msg.sender, vault_, to_, amount_);
    }

    // #endregion manager config as vault owner.

    /// @notice get vault of creator
    /// @param creator_ creator of Arrakis V2 vault
    /// @param index index on the list of vaults
    /// @return address of the Arrakis Vault V2
    function vaults(address creator_, uint256 index)
        external
        view
        returns (address)
    {
        require(_vaults[creator_].length() > index, "PALMTerms: out of bonds.");

        return _vaults[creator_].at(index);
    }

    // #region internals setter.

    function _addVault(address creator_, address vault_) internal {
        require(!_vaults[creator_].contains(vault_), "PALMTerms: vault exist");

        _vaults[creator_].add(vault_);
        emit AddVault(creator_, vault_);
    }

    function _setDelegate(address vault_, address delegate_) internal {
        require(
            delegateByVaults[vault_] != delegate_,
            "PALMTerms: already delegate"
        );

        delegateByVaults[vault_] = delegate_;
        emit DelegateVault(msg.sender, vault_, delegate_);
    }

    // #endregion internals setter.
}

contracts/functions/FPALMTerms.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import {
    IArrakisV2
} from "@arrakisfi/v2-core/contracts/interfaces/IArrakisV2.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {FullMath} from "@arrakisfi/v3-lib-0.8/contracts/FullMath.sol";

function _burn(IArrakisV2 vault_, address me)
    returns (
        uint256 amount0,
        uint256 amount1,
        uint256 balance
    )
{
    balance = IERC20(address(vault_)).balanceOf(me);
    (amount0, amount1) = vault_.burn(balance, me);
}

function _getInits(
    uint256 mintAmount_,
    uint256 amount0_,
    uint256 amount1_
) pure returns (uint256 init0, uint256 init1) {
    init0 = FullMath.mulDiv(amount0_, 1e18, mintAmount_);
    init1 = FullMath.mulDiv(amount1_, 1e18, mintAmount_);
}

function _getEmolument(uint256 allocation_, uint16 emolument_)
    pure
    returns (uint256)
{
    return (allocation_ * emolument_) / 10000;
}

function _requireTokensAllocationsGtZero(uint256 amount0_, uint256 amount1_)
    pure
{
    require(amount0_ > 0 || amount1_ > 0, "PALMTerms: no tokens allocations.");
}

function _requireTknOrder(address token0_, address token1_) pure {
    require(token0_ < token1_, "PALMTerms: tokens order inverted.");
}

contracts/interfaces/IPALMManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0;

import {VaultInfo} from "../structs/SPALMManager.sol";
import {Rebalance} from "@arrakisfi/v2-core/contracts/structs/SArrakisV2.sol";

interface IPALMManager {
    event AddVault(address indexed vault, bytes datas, string strat);

    event RemoveVault(address indexed vault, uint256 sendBack);

    event SetVaultData(address indexed vault, bytes data);

    event SetVaultStrat(address indexed vault, bytes32 strat);

    event SetManagerFeeBPS(address indexed vault, uint16 managerFeeBPS);

    event WhitelistStrat(string strat);

    event AddOperators(address[] operators);

    event RemoveOperators(address[] operators);

    event UpdateVaultBalance(address indexed vault, uint256 newBalance);

    event SetGelatoFeeCollector(address gelatoFeeCollector);

    event SetTermEnd(
        address indexed vault,
        uint256 oldtermEnd,
        uint256 newtermEnd
    );

    event WithdrawVaultBalance(
        address indexed vault,
        uint256 amount,
        address to,
        uint256 newBalance
    );

    event RebalanceVault(address indexed vault, uint256 newBalance);

    // ======== GELATOFIED FUNCTIONS ========
    function rebalance(
        address vault_,
        Rebalance calldata rebalanceParams_,
        uint256 feeAmount_
    ) external;

    // ======= PERMISSIONED OWNER FUNCTIONS =====
    function withdrawVaultBalance(
        address vault_,
        uint256 amount_,
        address payable to_
    ) external;

    function addVault(
        address vault_,
        bytes calldata datas_,
        string calldata strat_
    ) external payable;

    function removeVault(address vault_, address payable to_) external;

    function setVaultData(address vault_, bytes calldata data_) external;

    function setVaultStratByName(address vault_, string calldata strat_)
        external;

    function setGelatoFeeCollector(address payable gelatoFeeCollector_)
        external;

    function setManagerFeeBPS(address vault_) external;

    function addOperators(address[] calldata operators_) external;

    function removeOperators(address[] calldata operators_) external;

    function pause() external;

    function unpause() external;

    function withdrawFeesEarned(address[] calldata tokens_, address to_)
        external;

    // ======= PUBLIC FUNCTIONS =====
    function fundVaultBalance(address vault_) external payable;

    function renewTerm(address vault_) external;

    function getVaultInfo(address vault_)
        external
        view
        returns (VaultInfo memory);

    function getWhitelistedStrat() external view returns (bytes32[] memory);

    function vaults(address vault_)
        external
        view
        returns (
            uint256 balance,
            uint256 lastBalance,
            bytes memory datas,
            bytes32 strat,
            uint256 termEnd
        );

    function getOperators() external view returns (address[] memory);

    function managerFeeBPS() external view returns (uint16);
}

